import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import LoginClient from './components/Auth/LoginClient';
import ListeSinistres from './components/Sinistre/ListeSinistres';
import Navbar from './components/Layout/Navbar';
import GestionUtilisateurs from './components/Utilisateur/GestionUtilisateurs';
import AjoutRapport from './components/Rapport/AjoutRapport';
import ListeNotifications from './components/Notification/ListeNotifications';

function App() {
  const [clientId, setClientId] = useState(null); // null par défaut

  return (
    <BrowserRouter>
      <Navbar />
      <div className="container">
        <Routes>
          <Route path="/" element={<LoginClient setClientId={setClientId} />} />
          <Route path="/mes-sinistres" element={<ListeSinistres clientId={clientId} />} />
          <Route path="/utilisateurs" element={<GestionUtilisateurs />} />
          <Route path="/ajout-rapport-expertise" element={<AjoutRapport type="expertise" />} />
          <Route path="/ajout-rapport-reparation" element={<AjoutRapport type="reparation" />} />
          <Route path="/notifications" element={<ListeNotifications utilisateurId={clientId} />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
